import React from 'react';
import { Link } from 'react-router-dom';
// ...aquí irá el sidebar fijo con links a todas las páginas...
export default function Sidebar() {
    return (
        <aside style={{ position: 'fixed', top: 70, left: 0, width: 220, height: 'calc(100vh - 70px)', background: '#eaeaea', zIndex: 99 }}>
            <nav style={{ display: 'flex', flexDirection: 'column', padding: 20 }}>
                <Link to="/stories">Historias</Link>
                <Link to="/create-story">Crear Historia</Link>
                <Link to="/news">Noticias</Link>
                <Link to="/statistics">Estadísticas</Link>
                <Link to="/contests-trophies">Concursos y Trofeos</Link>
                <Link to="/profile">Perfil</Link>
            </nav>
        </aside>
    );
}
