import React, { useState, useEffect } from 'react';
export default function Profile() {
    // Simulación de datos del usuario (en producción, obtener de backend)
    const [avatar, setAvatar] = useState(null);
    const [centroTipo, setCentroTipo] = useState('CEIP');
    const [centroNombre, setCentroNombre] = useState('');
    const [usuario, setUsuario] = useState({ nombre: '', nick: '', email: '' });
    // Simulación: obtener email del usuario logueado
    const email = localStorage.getItem('email') || 'juanp@example.com';

    useEffect(() => {
        fetch(`http://localhost:5000/api/perfil/${email}`)
            .then(res => res.json())
            .then(data => {
                if (data.ok) {
                    setUsuario(data.usuario);
                    setCentroTipo(data.usuario.centroTipo || 'CEIP');
                    setCentroNombre(data.usuario.centroNombre || '');
                    setAvatar(data.usuario.avatar || null);
                }
            });
    }, [email]);

    const guardarCentro = async (nuevoTipo, nuevoNombre) => {
        setCentroTipo(nuevoTipo);
        setCentroNombre(nuevoNombre);
        await fetch(`http://localhost:5000/api/perfil/${email}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                nombre: usuario.nombre,
                apellido: usuario.apellido,
                nick: usuario.nick,
                avatar,
                centroTipo: nuevoTipo,
                centroNombre: nuevoNombre
            })
        });
    };
    const handleAvatar = (e) => {
        if (e.target.files && e.target.files[0]) {
            const reader = new FileReader();
            reader.onloadend = async () => {
                setAvatar(reader.result);
                await fetch(`http://localhost:5000/api/perfil/${email}`, {
                    method: 'PUT',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                        nombre: usuario.nombre,
                        apellido: usuario.apellido,
                        nick: usuario.nick,
                        avatar: reader.result,
                        centroTipo,
                        centroNombre
                    })
                });
            };
            reader.readAsDataURL(e.target.files[0]);
        }
    };
    return (
        <div style={{ display: 'flex', flexDirection: 'column', padding: 40 }}>
            <div style={{ display: 'flex', width: '100%' }}>
                <div style={{ width: '60%', minWidth: 300, background: '#f9f9f9', padding: 20 }}>
                    <h3>Crear avatar</h3>
                    <input type="file" accept="image/*" onChange={handleAvatar} />
                    {avatar && <img src={avatar} alt="avatar" style={{ width: 100, height: 100, borderRadius: '50%', marginTop: 10 }} />}
                    <h3 style={{ marginTop: 30 }}>Datos personales</h3>
                    <div><b>Nombre completo:</b> {usuario.nombre}</div>
                    <div><b>Nick:</b> {usuario.nick}</div>
                    <div><b>Email:</b> {usuario.email}</div>
                    <div style={{ marginTop: 20 }}>
                        <label>Centro escolar:</label><br />
                        <select value={centroTipo} onChange={e => setCentroTipo(e.target.value)}>
                            <option value="CEIP">CEIP</option>
                            <option value="IES">IES</option>
                        </select>
                        <input type="text" placeholder="Nombre del centro" value={centroNombre} onChange={e => guardarCentro(centroTipo, e.target.value)} style={{ marginLeft: 10 }} />
                        {/* Guardar tipo de centro al cambiar */}
                        <script>{`
                            document.querySelector('select').onchange = function(e) {
                                window.guardarCentro && window.guardarCentro(e.target.value, document.querySelector('input[type=text]').value);
                            }
                        `}</script>
                    </div>
                </div>
                <div style={{ width: '40%', minWidth: 200, background: '#f0f0f0', padding: 20 }}>
                    <h3>Trofeos</h3>
                    {/* Trofeos del usuario */}
                </div>
            </div>
            <div style={{ width: '100%', background: '#e0e0e0', padding: 20, marginTop: 20 }}>
                <h3>Chat privado</h3>
                {/* Chat privado */}
            </div>
            {/* Si es Docente, mostrar bloques de Noticias, Concursos y Panel de Administración aquí */}
        </div>
    );
}
