import React from 'react';
import { Link } from 'react-router-dom';
// ...aqu칤 ir치 el sidebar fijo con links a todas las p치ginas...
export default function Sidebar() {
    const items = [
        { to: '/', icon: '游', label: 'Inicio' },
        { to: '/stories', icon: '游닄', label: 'Historias' },
        { to: '/create-story', icon: '九꽲잺', label: 'Crear Historia' },
        { to: '/news', icon: '游닗', label: 'Noticias' },
        { to: '/statistics', icon: '游늵', label: 'Estad칤sticas' },
        { to: '/contests-trophies', icon: '游끥', label: 'Concursos y Trofeos' },
        { to: '/profile', icon: '游녻', label: 'Perfil' },
    ];
    return (
        <aside style={{ position: 'fixed', top: 70, left: 0, width: 220, height: 'calc(100vh - 70px)', background: '#eaeaea', zIndex: 99 }}>
            <nav style={{ display: 'flex', flexDirection: 'column', padding: 20, gap: 18, height: '100%', justifyContent: 'space-between' }}>
                {items.map((item, idx) => (
                    <Link key={idx} to={item.to} style={{ display: 'flex', alignItems: 'center', gap: 12, background: '#fff', borderRadius: 8, boxShadow: '0 1px 4px #0001', padding: '14px 10px', fontSize: '1.08rem', textDecoration: 'none', color: '#222', fontWeight: 500 }}>
                        <span style={{ fontSize: '1.5rem' }}>{item.icon}</span>
                        <span>{item.label}</span>
                    </Link>
                ))}
            </nav>
        </aside>
    );
}
