import React from 'react';
import { Link } from 'react-router-dom';
// ...aqu칤 ir치 el sidebar fijo con links a todas las p치ginas...
export default function Sidebar() {
    const items = [
        { to: '/stories', icon: '游닄', label: 'Historias' },
        { to: '/create-story', icon: '九꽲잺', label: 'Crear Historia' },
        { to: '/news', icon: '游닗', label: 'Noticias' },
        { to: '/statistics', icon: '游늵', label: 'Estad칤sticas' },
        { to: '/contests-trophies', icon: '游끥', label: 'Concursos y Trofeos' },
        { to: '/profile', icon: '游녻', label: 'Perfil' },
    ];
    return (
        <aside style={{ position: 'fixed', top: 70, left: 0, width: 220, height: 'calc(100vh - 70px)', background: '#eaeaea', zIndex: 99 }}>
            <nav style={{ display: 'flex', flexDirection: 'column', padding: 20, height: '100%', justifyContent: 'space-between' }}>
                {items.map((item, idx) => (
                    <Link
                        key={item.to}
                        to={item.to}
                        style={{
                            display: 'flex',
                            flexDirection: 'column',
                            alignItems: 'center',
                            textDecoration: 'none',
                            color: '#222',
                            marginBottom: idx < items.length - 1 ? 18 : 0,
                            background: '#fff',
                            borderRadius: 10,
                            boxShadow: '0 1px 6px #0001',
                            padding: '18px 0',
                            fontWeight: 500,
                            fontSize: '1.08rem',
                            transition: 'background 0.2s',
                        }}
                    >
                        <span style={{ fontSize: '2rem', marginBottom: 6 }}>{item.icon}</span>
                        {item.label}
                    </Link>
                ))}
            </nav>
        </aside>
    );
}
